### 结构体
##### C99结构体声明方式
    #include <stdio.h>

    struct shijian {
        int shi;
        int fen;
        int miao;
    };    

    int main(){
        struct shijian sj = {.shi=10, .fen=11, .miao=12};
        struct shijian js = (struct shijian){.shi=14, .fen=14, .miao=15};
        printf("%d\n", sj.miao);
        printf("%d\n", js.fen);
        return 0;
    }

### 共用体 union
示例：
    #include <stdio.h>

    union U {
        int i;
        char j;
        long long k;
    };


    int main(){
        union U u = {100};// 只能初始化赋值第一个
        printf("u.i:%d\n", u.i);
        printf("u.j:%c\n", u.j);
        printf("u.k:%ll\n", u.k);
        printf("------\n");

        u.j = 'a';
        printf("u.i:%d\n", u.i);
        printf("u.j:%c\n", u.j);
        printf("u.k:%ll\n", u.k);
        printf("------\n");

        u.k = 1223;
        printf("u.i:%d\n", u.i);
        printf("u.j:%c\n", u.j);
        printf("u.k:%ll\n", u.k);
        printf("------\n");

        return 0;
    }
