        print_number(5);
    }

    fn print_number(x: int) {
        println!("x is: {}", x);
    }
    // 多参数调用
    fn main() {
        print_sum(5, 6);
    }

    fn print_sum(x: int, y: int) {
        println!("sum is: {}", x + y);
    }
